mapper.select:
SELECT c.company_name, c.ceo_name, c.phone, c.email, c.zipcode, c.address1, c.address2, c.homepage, c.region_id, pc.id AS pc_id, pc.description_html, pc.status, pc.category_id, pc.created_at AS partner_created_at, pc.updated_at AS partner_updated_at, pc.deleted_at AS partner_deleted_at, cf.id AS cf_id, cf.company_id, cf.file_id, f.id AS f_id, f.file_path, f.original_name, f.file_name, f.mime_type, f.file_size, f.created_at AS file_created_at, rc.name AS region_name FROM partner_company pc LEFT JOIN company c ON pc.id = c.id LEFT JOIN company_file cf ON pc.id = cf.company_id LEFT JOIN file f ON cf.file_id = f.id LEFT JOIN region_category rc ON c.region_id = rc.id
AND pc.id = #{id}
AND pc.category_id = #{categoryId}
ORDER BY cf.file_type ASC;

SELECT c.company_name, c.ceo_name, c.phone, c.email, c.zipcode, c.address1, c.address2, c.homepage, c.region_id, pc.id AS pc_id, pc.description_html, pc.status, pc.category_id, pc.created_at AS partner_created_at, pc.updated_at AS partner_updated_at, pc.deleted_at AS partner_deleted_at, cf.id AS cf_id, cf.company_id, cf.file_id, f.id AS f_id, f.file_path, f.original_name, f.file_name, f.mime_type, f.file_size, f.created_at AS file_created_at, rc.name AS region_name FROM partner_company pc LEFT JOIN company c ON pc.id = c.id LEFT JOIN company_file cf ON pc.id = cf.company_id LEFT JOIN file f ON cf.file_id = f.id LEFT JOIN region_category rc ON c.region_id = rc.id
AND pc.id = #{id}
AND pc.category_id IN ( SELECT id FROM category WHERE parent_id = #{categoryId} )
AND pc.category_id = #{categoryId}
AND pc.status = #{status}
ORDER BY cf.file_type ASC;
