mapper.insert:
INSERT INTO users ( id, password, name, nick_name, phone_mobile, phone_home, email, question1_id, answer1, question2_id, answer2, sms_agree, email_agree, ad_agree, terms_agree
, role
) VALUES ( #{id}, #{password}, #{name}, #{nickName}, #{phoneMobile}, #{phoneHome}, #{email}, #{question1Id}, #{answer1}, #{question2Id}, #{answer2}, #{smsAgree}, #{emailAgree}, #{adAgree}, #{termsAgree}
, #{role}
)

mapper.select:
SELECT id
FROM users
WHERE name = #{name}
  AND email = #{email}
  AND deleted_at IS NULL

SELECT COUNT(*)
FROM users
WHERE name = #{name}
  AND email = #{email}
  AND id = #{id}
  AND deleted_at IS NULL
